  if (typeof window.makeStickyHeader !== 'function') {
    window.getStuckHeaderHeight = function () {
      var siteHeader = document.querySelector('.SiteHeader');
      var siteHeaderHeight = (siteHeader)
        ? siteHeader.offsetHeight
        : 0;
