              return;
            },
            enter: function(data) {
                const tippyPopper = document.querySelector('.tippy-popper')

                if (tippyPopper) {
                  tippyPopper.remove()
                 }

              return gsap.timeline()
                .set(data.current.container, {position: 'absolute', opacity: 0})
                .from(data.next.container, {
                  opacity: 0,
                  duration: 0.2,
                }, '<')
                .to('.SiteFooter', {
                  opacity: 1,
