    var onClickParent = function (event) {

      var parentLink = $(event.target).closest('.MobileNav__link');
      var dropdown = $(parentLink).next('.js-mobile-dropdown')[0];

      if (dropdown) {
        if ($(parentLink).hasClass('active')) {
          clickAndClose();
