          gsap.from(dropdown, 0.6, {height: 0});
          gsap.from(dropdown.querySelectorAll('.MobileNav__subLink'), {
            duration: 0.8,
            opacity: 0,
            scale: 0.5,
            stagger: 0.1,
            ease: 'power3'
          })
        }
      } else {
        clickAndClose()
      };
    };

    $('.MobileNav__link').click(onClickParent);
    $('.MobileNav__subLink').click(clickAndClose);
  });

